//FLEXBOX AND GRID

@mixin flexbox {
    display: flex;
}

@mixin flexbox-c-sb {
    display: flex;
    align-items: center;
    justify-content: space-between;
}

@mixin flexbox-c-c {
    display: flex;
    justify-content: center;
    align-items: center;
}

@mixin flexbox-c-sa {
    display: flex;
    align-items: center;
    justify-content: space-around;
}

@mixin flexbox-sa-sa {
    display: flex;
    align-items: center;
    align-content: space-around;
    justify-content: space-around;
}

@mixin grid-center {
    display: grid;
    place-items: center;
}

//BUTTONS
@mixin button {
    height: 50px;
    width: 150px;
    font-size: 1.6rem;
    font-weight: $bold;
    border-radius: 20px;
    border: none;
    background-color: $secondaryCol;
    box-shadow: rgba(100, 100, 111, 0.2) 0px 7px 29px 0px;
    cursor: pointer;
    @include transition;
    &:hover {
        transform: translateY(-5px);
        border: 1px solid black;
    }
}

@mixin transition {
    transition: 0.3s ease-in-out;
}

