@import "../abstracts/mixins.scss";

.about-container {
    max-width: 1400px;
    margin: 0 auto;
    height: 80vh;
    @include flexbox;

    .about-title-container {
        @include flexbox-c-c;
        flex: 0.65;
        height: 100%;
    }

    .about-bio-container {
        flex: 1;
        margin: auto 4rem;
        padding: 10rem 0;
        width: 40%;

        h3 {
            margin-bottom: 2rem;
        }

        .skills {
            margin-top: 2rem;
        }

        .skill-icons-container {

            // height: 15rem;

            ul {
                height: 100%;
                @include flexbox-sa-sa;

                li {
                    height: 8rem;
                    width: 8rem;
                   

                    @include flexbox-c-c;

                    i  {
                        font-size: 8rem;
                        
                    }

                    .fa-brands {
                        color: black;
                    }
                }
            }
        }
    }

    .line {
        margin: auto 0;
        width: 2px;
        height: 60%;
        background-color: $secondaryCol;
        position: relative;
    }
}

sources.scss\\layouts\\_project.scss:
.projects-container {
    margin: 5rem auto;
    max-width: 1200px;
    @include flexbox;
    align-items: center;
    flex-direction: column;

    h2 {
        margin-bottom: 5rem;
    }

    .project {
        @include flexbox;
        width: 100%;
        border-radius: 20px;
        box-shadow: rgba(100, 100, 111, 0.2) 0px 7px 29px 0px;

        &:nth-child(2), &:nth-child(3) {
            margin-bottom: 10rem;
        }

        .info {
            flex: 1;
            padding: 1.5rem;
            margin: auto 0;
            h3 { 
                margin-bottom: 1.75rem;

                &:nth-child(2) {
                    margin-top: 1.75rem;
                }
            }
            div {   
                width: 100%;
                @include flexbox;
                justify-content: center;

                button {
                    @include button;
                    @include flexbox-c-c;
                    margin: 2rem 1.2rem 0 1.2rem;
                }
            }
        }

        .image {
            flex: 1;
            height: 400px;
            @include grid-center;
        }
    }
}

